import React, { useState } from "react";
import { Navigate } from "react-router-dom";
import CONSTANTS from "../data/config";

const AddStudentForm = ({ student }) => {
  const [firstName, setFirstName] = useState(student ? student.firstName : "");
  const [lastName, setLastName] = useState(student ? student.lastName : "");
  const [school, setSchool] = useState(student ? student.school : "");
  const [addFlag, setAddFlag] = useState(false);

  const handleSubmit = async (event, action) => {
    event.preventDefault();
    let url = `${CONSTANTS.BASE_API_URL}students/`;
    let method = 'POST';
    let body = JSON.stringify({ firstName, lastName, school });

    if (action === 'update') {
      url += `${student.id}`;
      method = 'PUT';
    } else if (action === 'delete') {
      url += `${student.id}`;
      method = 'DELETE';
      body = undefined;
    }

    try {
      const response = await fetch(url, {
        method: method,
        body: body,
        headers: { "Content-Type": "application/json" },
      });
      const data = await response.json();
      console.log(data);
      setAddFlag(true);
    } catch (error) {
      console.error("Error:", error);
      // Here you could set an error state and display it to the user
    }
  };

  if (addFlag) {
    return <Navigate to={{ pathname: "/list", state: { refresh: true } }} />;
  }

  return (
    <React.Fragment>
      <div className="panel panel-default">
        <form onSubmit={(e) => handleSubmit(e, 'add')}>
          <h3>Add, Update, Delete Student</h3>
          {/* ... other form fields ... */}

          <button type="submit" className="btn btn-success">
            Add
          </button>
          <button type="button" onClick={(e) => handleSubmit(e, 'update')} className="btn btn-success">
            Update
          </button>
          <button type="button" onClick={(e) => handleSubmit(e, 'delete')} className="btn btn-danger">
            Delete
          </button>
        </form>
      </div>
    </React.Fragment>
  );
};

export default AddStudentForm;
